(365, 1, '2019-03-09 15:55:29', '2019-03-09 12:55:29', '<!-- wp:paragraph -->\n<p>• &nbsp;Праздник наката </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День девяти жизней </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День рождения Земли - Вешнее Макошье </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День основания Российской книжной палаты </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День книги - Германия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Национальный день креветок - США </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Праздник цветов - Азербайджан </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Национальный день общественных парков - США </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День хлебопашца - Таиланд </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День мельника - Голландия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День матери - Мексика, Гватемала, Сальвадор </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День банковского работника - Кыргызстан </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День национальной независимости - Румыния </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День конституции - Микронезия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Именины у Семена, Марии, Николая, Степана,\nИвана, Петра, Анастасии, Сергея, Павла </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Семен Ранопашец </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 10 Мая </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;10 мая 1503 года испанский мореплаватель\nХристофор Колумб открыл Каймановы острова </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;10 мая 1883 года заго