(367, 1, '2019-03-09 15:57:38', '2019-03-09 12:57:38', '<!-- wp:paragraph -->\n<p>• &nbsp;Международный день матери (Mother`s Day) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День Чебурашки </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День жён-мироносиц </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Международный день медицинской сестры\n(International Nurses Day) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День ежиного сопения </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День осведомлённости о синдроме хронической\nусталости </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День экологического образования </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Фестиваль котов - Бельгия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День Государственного флага и герба - Беларусь\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Национальный день ореховой помадки - США </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День финского самосознания </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День памяти святого апостола Андрея\nПервозванного - Грузия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День железнодорожника - Сербия </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День Шашмакома - Таджикистан </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День армии - Республика Сербская </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День святого Василия Острожского - Черногория </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;Именины у Артема, Василия, Феодота, Богдана,\nФилимона, Ивана </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• &nbsp;День Девяти мучеников </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События