(644, 1, '2019-03-27 15:06:13', '2019-03-27 12:06:13', '<!-- wp:paragraph -->\n<p>&nbsp;• Праздник воспаленного воображения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День выгула черепахи</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Начало Успенского поста</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день ящерицы</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День подразделения по борьбе с\nпреступлениями в сфере информационных технологий (Управление «К») МВД РФ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения португальской корриды</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день фруктового мороженого -\nСША</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День памяти защитников Отечества - Абхазия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День инженера - Доминикана</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День скаутов - Индонезия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Фолклендских островов</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День независимости - Пакистан</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день флага - Парагвай</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День победы над Японией - Великобритания</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День исламского сопротивления - Иран</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Антонина, Леонтия, Гурия,\nЕлеазара, Александра, Киндея, Дмитрия, Тимофея, Софьи</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Медовый спас, Мокрый Маккавей </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 14 Августа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1822 года в России издан указ о\nзапрещении тайных обществ и закрытии масонских лож</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1850 года основан\nНиколаевск-на-Амуре</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1865 года в Санкт-Петербурге\nсостоялось открытие Зоологического сада</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1888 года в США запатентован\nэлектрический счетчик</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1893 года в Париже выдано первое\nв мире водительское удостоверение</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1908 года в Англии состоялся\nпервый международный конкурс красоты</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1944 года созданы Тюменская и\nВладимирская области</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1992 года в России началась\nчековая приватизация</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 14 августа 1994 года космический телескоп\nХаббл впервые сфотографировал кольца Урана </p>\n<!-- /wp:paragraph -->', 'Праздники – 14 Августа', '', 'publish', 'open', 'closed', '', 'prazdniki-14-avgusta', '', '', '2019-03-27 15:06:13', '2019-03-27 12:06:13', '', 0, 'http://current-news/?p=644', 0, 'post', '', 0),
