(654, 1, '2019-03-27 15:10:02', '2019-03-27 12:10:02', '<!-- wp:paragraph -->\n<p>&nbsp;• Преображение Господне</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения русской тельняшки</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день фотографии (World Photography\nDay)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День превращения в бабочку</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Мотострелковых войск России</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День филателии (День почтовой марки)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день гуманитарной помощи (World\nHumanitarian Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Международный день орангутанга\n(International Orangutan Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День пасечника - Украина</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день картофеля - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день авиации - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День гражданской защиты - ПМР</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День независимости - Афганистан</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Августовской революции - Вьетнам</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Архипа, Макара, Михаила, Петра,\nМитрофана</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Яблочный спас </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 19 Августа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 августа 1839 года открылась Пулковская\nобсерватория</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 августа 1856 года в США з