(656, 1, '2019-03-27 15:10:39', '2019-03-27 12:10:39', '<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день лени</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения Чебурашки</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день комара (World Mosquito Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День музыкальных шкатулок</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День укрепления тылов</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день лимонада - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День возобновляемых источников энергии -\nИндия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День святого Иштвана - Венгрия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день шоколадного пирога с\nпеканом (National Chocolate Pecan Pie Day) - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день радио - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День восстановления независимости - Эстония</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День народной революции - Марокко</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Никанора, Алексея, Василия,\nМитрофана, Антона, Михаила, Пимена, Афанасия, Ивана, Петра, Александра, Дмитрия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Пимены-Марины </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>События в истории – 20 Августа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1634 года в Москве издан первый\nбукварь</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1721 года запущены первые фонтаны\nи каскады Петергофа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1741 года первым русским\nмореплавателем Витусом Берингом открыта Аляска</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1896 года запатентован телефон с\nнаборным диском</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1912 года запатентован фонограф и\nэлектробатарея</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1913 года в Киеве началась Первая\nВсероссийская спортивная олимпиада</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1922 года из Москвы началась\nшироковещательная трансляция радиопередач</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1925 года в СССР создано Общество\nдрузей советской кинематографии</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 20 августа 1951 года в СССР основано ДОСААФ\n(Добровольное общество содействия армии, авиации и флоту)\n\n&nbsp;• 20 августа\n1977 года в США стартовал космический корабль «Voyager 2» для исследования\nдальних планет Солнечной системы \n\n\n\n</p>\n<!-- /wp:paragraph -->', 'Праздники – 20 Августа', '', 'publish', 'open', 'closed', '', 'prazdniki-20-avgusta', '', '', '2019-03-27 15:10:39', '2019-03-27 12:10:39', '', 0, 'http://current-news/?p=656', 0, 'post', '', 0),
(658, 1, '2019-03-27 15:11:12', '2019-03-27 12:11:12', '<!-- wp:paragraph -->\n<p>&nbsp;• День офицера России</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Стрибога</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День сбора диких трав</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Праздник старых бурдюков</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Международный день памяти и поминовения\nжертв терроризма</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День молодежи - Марокко</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день спумони - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Ниной Акина - Филиппины</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День истории железной дороги - Эстония</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День закона «О государственном статусе\nЛатвийской Республики»</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Емилиана, Григория, Федора,\nМирона, Иосифа, Зосимы, Николая, Леонида, Моисея, Германа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Мирон Ветрогон </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 21 Августа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1841 года запатентованы подъемные\nжалюзи</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1923 года создан Государственный плановый\nкомитет Совета Министров СССР (Госплан СССР)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1924 года открылся\nГосударственный Пушкинский заповедник</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1932 года в Венеции состоялся\nпервый международный кинофестиваль</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1973 года основана академия МВД\nСССР</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 21 августа 1981 года ученые впервые сообщили\nоб угрозе глобального потепления на Земле </p>\n<!-- /wp:paragraph -->', 'Праздники – 21 Августа', '', 'publish', 'open', 'closed', '', 'prazdniki-21-avgusta', '', '', '2019-03-27 15:11:12', '2019-03-27 12:11:12', '', 0, 'http://current-news/?p=658', 0, 'post', '', 0),
(660, 1, '2019-03-27 15:11:54', '2019-03-27 12:11:54', '<!-- wp:paragraph -->\n<p>&nbsp;• День Государственного флага Российской\nФедерации</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День катания по радуге</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День дубовой бочки</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Республики Коми</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день риса джолоф (World Jollof\nRice Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День «Съешь персик» - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день бургера - Великобритания</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День торта с пеканом (National Pecan Torte\nDay) - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Петра, Макара, Ивана, Марии,\nЮлиана, Иакова, Самуила, Григория, Алексея, Ирины, Дмитрия, Леонтия, Маргариты,\nЯкова</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Матвей Ненастный </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 22 Августа</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1851 года в Австралии найдены\nзолотые месторождения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1864 года подписана первая\nЖеневская конвенция</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1865 года запатентовано жидкое\nмыло</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1909 года во Франции состоялось\nпервое в мире авиашоу</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1989 года астрономы обнаружили\nкольца Нептуна</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 22 августа 1990 года началось вещание\nнезависимой радиостанции «Эхо 