(716, 1, '2019-03-27 15:33:40', '2019-03-27 12:33:40', '<!-- wp:paragraph -->\n<p>Праздники – 19 Сентября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения cмайлика</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День выгуливания настроения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День оружейника</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Международный день подражания пиратам\n(International Talk Like A Pirate Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения домена .SU</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день бестраншейных технологий</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День конституции - Непал</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День армии - Чили</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День работника гражданской авиации - Молдова</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День независимости - Сент-Китс и Невис</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Архипа, Давида, Дмитрия, Кирилла,\nМакария, Феклы, Дениса, Михаила, Андрея, Константина, Ивана</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Михайлово чудо </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 19 Сентября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 сентября 1838 года запатентован\nжелезнодорожный тормоз («тормозной башмак»)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 сентября 1864 года в США запатентованы\nоткидные сиденья в машине</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 сентября 1888 года в Бельгии состоялся\nпервый всемирный конкурс красоты</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 19 сентября 1969 года впервые сов