ragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День ассоциации ветеранов МВД - Украина</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День вооружённых сил - Черногория</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День территории - Остров Рождества</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День благодарения - Сент-Люсия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День добычи торфа - Фолклендские острова</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Владислава, Галактиона, Андрея,\nПавла, Марии, Сергея</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Фёкла Заревница </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 7 Октября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1604 года основан город Томск</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1806 года получен патент на\nкопировальную бумагу</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1826 года открылась первая в США\nжелезная дорога</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1930 года основан Московский\nгосударственный агроинженерный университет (МГАУ)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1940 года Красногорск получил\nстатус города</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1949 года образована Германская\nДемократическая Республика (ГДР)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1959 года советская автоматическая\nмежпланетная станция «Луна-3» впервые в мире совершила полный облет Луны и\nсфотографировала обратную сторону</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 7 октября 1999 года обнаружен первый в мире\nкомпьютерный вирус в области системных драйверов, самого высо