(1083, 1, '2019-04-01 14:59:56', '2019-04-01 11:59:56', '<!-- wp:paragraph -->\n<p>&nbsp;• День закладываний</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Международный день девочек\n(International Day of the Girl Child)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День вдыхания осеннего воздуха</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Международный день каминг-аута</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день яйца (World Egg Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Республики Башкортостан</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День памяти преподобного Харитона\nИсповедника</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день пиццы с колбасой - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День посадки деревьев - Намибия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День вооружённых сил - Абхазия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день боливийской женщины</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День народного восстания - Македония</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Прохора, Василия, Феодора, Ильи,\nГригория, Анатолия, Александра, Татьяны, Вячеслава, Марка, Алексея, Афанасия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Харитонов день </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 11 Октября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 11 октября 1783 года в Петербурге учреждена\nРоссийская академия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 11 октября 1881 года в США запатентована\nфотопленка</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 11 октября 1887 года запатентована\nэлектричес