(1984, 1, '2019-09-27 08:54:48', '2019-09-27 05:54:48', '<!-- wp:paragraph -->\n<p>&nbsp;• День просящихся рюмок</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День встречи со старыми друзьями</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день моря (World Maritime Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день контрацепции (World\nContraception Day)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Всемирный день здоровья окружающей среды</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День рождения граммофона</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Международный день борьбы за полную\nликвидацию ядерного оружия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Европейский день языков (European\nDay of Languages)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;•\nДень независимости - Новая Зеландия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Сентябрьской революции - Йемен</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День Джонни Эпплсида - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День национального флага - Эквадор</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день американских блинов\n(National Pancake Day) - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День пельменей (National\nDumpling Day) - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;•\nИменины у Ильи, Николая, Леонтия, Лукьяна, Петра, Юлиана, Александра</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Корнилов день </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 26 Сентября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 26 сентября 1896 года основано Московское\nинженерное училище ведомства путей сообщения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 26 сентября 1920 года основана\nвоенно-воздушная инженерная академия имени Н. Е. Жуковского</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 26 сентября 1995 года Украина принята в\nСовет Европы</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 26 сентября 2008 года в Монголии открыта\nогромная конная статуя Чингисхана </p>\n<!-- /wp:paragraph -->', 'Праздники – 26 Сентября', '', 'inherit', 'closed', 'closed', '', '730-revision-v1', '', '', '2019-09-27 08:54:48', '2019-09-27 05:54:48', '', 730, 'http://dayinfo.ru/2019/09/27/730-revision-v1/', 0, 'revision', '', 0),
