(1994, 1, '2019-10-08 11:51:51', '2019-10-08 08:51:51', '<!-- wp:paragraph -->\n<p>&nbsp;• День солнечных улыбок</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День командира надводного, подводного и\nвоздушного корабля</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День юриста - Украина</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День детей - Иран</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Национальный день вареников - США</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День танкиста - Армения</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День посадки деревьев - Гуам</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День независимости - Хорватия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День героического партизана - Куба</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День военно-воздушных сил - Индия</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День сельского хозяйства - Таджикистан</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• День военно-морских сил - Перу</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Именины у Германа, Евгения, Максима,\nНиколая, Павла, Прохора, Романа, Сергея, Бригиды, Пелагеи, Людвика</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• Сергей Капустник </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {"fontSize":"large"} -->\n<p class="has-large-font-size">События в истории – 8 Октября</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 8 октября 1818 года в Англии впервые\nприменены в бою боксерские перчатки</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 8 октября 1896 года впервые опубликован\nпромышленный индекс Доу-Джонса</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 8 октября 1926 года состоялся первый в мире\nфутбольный репортаж по радио</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;• 8 октября 1961 года состоялось открытие\nГосударственного Кремлевского дворца съездов </p>\n<!-- /wp:paragraph -->', 'Праздники – 8 Октября', '', 'inherit', 'closed', 'closed', '', '1077-revision-v1', '', '', '2019-10-08 11:51:51', '2019-10-08 08:51:51', '', 1077, 'http://dayinfo.ru/2019/10/08/1077-revision-v1/', 0, 'revision', '', 0);
